angular.module('kityminderEditor')
    .filter('sizer', function() {

        return function(size, block) {

            if (size < 1024) {
                return size + ' B';
            } else if (size < 1024 * 1024) {
                return (size / 1024).toFixed(2) + ' K';
            } else if (size < 1024 * 1024 * 1024) {
                return (size / (1024 * 1024)).toFixed(2) + ' M';
            } else {
                return '';
            }

        };
    });
angular.module('kityminderEditor')
    .filter('timer', function() {

        return function(text, block) {

            if (!text) {
                return '';
            }

            var curTime = new Date();
            var fileTime = new Date(text.replace(/-/g, '/'));
            var diff = curTime - fileTime;

            var second = Math.round(diff / 1000);
            var minute = Math.round(second / 60);

            if (minute < 5) {
                return '';
            } else if (minute < 60) {
                return minute + ' ';
            } else {
                var hour = Math.round(minute / 60);

                if (hour < 24) {
                    return hour + ' ';
                } else {
                    var day = Math.round(hour / 24);

                    if (day < 2) {
                        return '';
                    } else if (day < 90) {
                        return day + ' ';
                    } else if (fileTime.getFullYear() === curTime.getFullYear()) {
                        return (fileTime.getMonth() + 1) + '' + fileTime.getDate() + '';
                    } else {
                        return '';
                        //return fileTime.getFullYear() + ''+ (fileTime.getMonth() + 1) + '' + fileTime.getDate() + '';
                    }
                }

            }
        };
    });
