angular.module('kityminderEditor')
    .service('cookie', function() {

        function getCookie(key) {
            var strCookie = document.cookie;
            var arrCookie = strCookie.split("; ");

            for(var i = 0; i< arrCookie.length; i++) {
                //var arr = ele.split('=');

                var arr = arrCookie[i].split('=');
                if(arr[0] === key) {
                    return decodeURIComponent(arr[1]);
                }
            }

            return '';

        }

        function setCookie(key, value, expireHours){
            var cookieString = key + "=" + encodeURIComponent(value);
            //

            if(expireHours > 0){
                var date = new Date();
                date.setTime(date.getTime + expireHours * 3600 * 1000);
                cookieString = cookieString + "; expire=" + date.toGMTString();
            }
            document.cookie = cookieString;
        }

        function getItemValue(key, cookieItem) {
            if (cookieItem.length > 0) {
                var startIndex = cookieItem.indexOf(key + "=");

                if (startIndex != -1) {

                    startIndex = startIndex + key.length + 1;

                    var endIndex = cookieItem.indexOf("&", startIndex);
                    if (endIndex == -1) {
                        endIndex = cookieItem.length;
                    }
                    return decodeURIComponent(cookieItem.substring(startIndex,endIndex));
                }
            }

            return '';
        }

        return {
            get: getCookie,
            set: setCookie,
            getItemValue: getItemValue
        }
    });
